import { useState, useEffect } from "react";
import { Navbar, Nav, Container } from "react-bootstrap";
import logo from '../assets/img/logo.png';
import navIcon1 from '../assets/img/nav-icon-github.svg';
import navIcon2 from '../assets/img/nav-icon-email.svg';
import navIcon3 from '../assets/img/nav-icon-whatsapp.svg';
import navIcon4 from '../assets/img/nav-icon-linkedin.svg';
import { HashLink } from 'react-router-hash-link';

export const NavBar = () => {

  const [activeLink, setActiveLink] = useState('home');
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const onScroll = () => {
      if (window.scrollY > 50) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    }

    window.addEventListener("scroll", onScroll);

    return () => window.removeEventListener("scroll", onScroll);
  }, [])

  const onUpdateActiveLink = (value) => {
    setActiveLink(value);
  }

  return (
    <Navbar expand="md" className={scrolled ? "scrolled" : ""}>
        <Container>
          <Navbar.Brand href="/">
            <img src={logo} alt="Logo" />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav">
            <span className="navbar-toggler-icon"></span>
          </Navbar.Toggle>
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="ms-auto">
              <Nav.Link href="#home" className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('home')}>Home</Nav.Link>
              <Nav.Link href="#skills" className={activeLink === 'skills' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('skills')}>Skills</Nav.Link>
              <Nav.Link href="#projects" className={activeLink === 'projects' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActiveLink('projects')}>Projects</Nav.Link>
            </Nav>
            <span className="navbar-text">
              <div className="social-icon">
                <a href="https://github.com/ManaR-Rch" target="_blank" rel="noopener noreferrer"><img src={navIcon1} alt="GitHub" /></a>
                <a href="mailto:manarmarchou6@gmail.com"><img src={navIcon2} alt="Email" /></a>
                <a href="https://wa.me/212600603449" target="_blank" rel="noopener noreferrer"><img src={navIcon3} alt="WhatsApp" /></a>
                <a href="https://linkedin.com/in/manar-marchoube-a955a9337/" target="_blank" rel="noopener noreferrer"><img src={navIcon4} alt="LinkedIn" /></a>
              </div>
              <a 
                href="/CV.pdf" 
                download="CV-Manar-Marchoube.pdf"
                className="cv-button"
              >
                <button className="cv-btn"><span>Download CV</span></button>
              </a>
              <HashLink to='#connect'>
                <button className="vvd"><span>Letâ€™s Connect</span></button>
              </HashLink>
            </span>
          </Navbar.Collapse>
        </Container>
      </Navbar>
    )
  }
