import { Container, Row, Col } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import projImg1 from "../assets/img/project-img1.jpg";
import projImg2 from "../assets/img/project-img2.png";
import projImg3 from "../assets/img/project-img3.png";
import projImg4 from "../assets/img/project-img4.png";
import projImg5 from "../assets/img/project-img5.png";
import projImg6 from "../assets/img/project-img6.png";
import projectVd1 from "../assets/img/project-vd1.mp4";
import colorSharp2 from "../assets/img/color-sharp2.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Projects = () => {

  const projects = [
    {
      title: "SmartShop",
      description: "REST API Backend - B2B E-Commerce",
      imgUrl: projImg1,
      link: "https://github.com/ManaR-Rch/SmartShop.git",
    },
    {
      title: "ClusteredDataWarehouse",
      description: "Spring Boot FX Deals Processing",
      imgUrl: projImg2,
      link: "https://github.com/ManaR-Rch/clustereDwarehouse.git",
    },
    {
      title: "S-taffHub",
      description: "Laravel HR Web Application",
      imgUrl: projImg3,
      link: "https://github.com/ManaR-Rch/S-taffHub.git",
    },
    {
      title: "Music-ally",
      description: "Lyrics Search Web App",
      imgUrl: projImg4,
      link: "https://music-ally-beta.vercel.app",
    },
    {
      title: "UberGo",
      description: "Laravel Ride-Sharing Application",
      imgUrl: projImg5,
      link: "https://github.com/ManaR-Rch/UberGo.git",
      videoLink: projectVd1,
    },
    {
      title: "Ruby Blog Project",
      description: "Rails CRUD Blog Application",
      imgUrl: projImg6,
      link: "https://github.com/ManaR-Rch/Ruby_blog_CRUD.git",
    },
  ];

  return (
    <section className="project" id="projects">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) =>
              <div className={isVisible ? "animate__animated animate__fadeIn": ""}>
                <h2>Projects</h2>
                <p>30+ professional projects delivered using modern technologies. Full-stack development specializing in web applications, REST APIs, and responsive designs. From enterprise solutions to innovative web platforms.</p>
                <div className="projects-intro">
                  <p className="projects-creative">
                    Here are some of my recent projects showcasing my expertise across different technologies and domains. Each project demonstrates my commitment to clean code, best practices, and delivering real-world solutions:
                  </p>
                </div>
                <Row>
                  {
                    projects.map((project, index) => {
                      return (
                        <ProjectCard
                          key={index}
                          {...project}
                          />
                      )
                    })
                  }
                </Row>
              </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2}></img>
    </section>
  )
}
