import { Container, Row, Col, Tab, Nav } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import projImg1 from "../assets/img/project-img1.png";
import projImg2 from "../assets/img/project-img2.png";
import projImg3 from "../assets/img/project-img3.png";
import colorSharp2 from "../assets/img/color-sharp2.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Projects = () => {
  const [activeFilter, setActiveFilter] = useState('all');

  const projects = [
    {
      title: "STAFFHUB",
      description: "HR Management System",
      imgUrl: projImg1,
      category: "fullstack",
      tech: ["Laravel", "PostgreSQL", "Tailwind CSS"],
      icon: "ðŸ‘¥"
    },
    {
      title: "Delivery Optimizer",
      description: "Logistics System",
      imgUrl: projImg2,
      category: "java",
      tech: ["Java Spring Boot", "REST API", "JUnit"],
      icon: "ðŸšš"
    },
    {
      title: "Full-Stack Application",
      description: "RESTful Web Service",
      imgUrl: projImg3,
      category: "fullstack",
      tech: ["React.js", "Node.js", "MongoDB"],
      icon: "âš™ï¸"
    },
    {
      title: "E-Commerce Platform",
      description: "Multi-vendor System",
      imgUrl: projImg1,
      category: "laravel",
      tech: ["Laravel", "MySQL", "Bootstrap"],
      icon: "ðŸ›’"
    },
    {
      title: "Mobile Web App",
      description: "React Native Project",
      imgUrl: projImg2,
      category: "fullstack",
      tech: ["React Native", "Firebase", "Expo"],
      icon: "ðŸ“±"
    },
    {
      title: "Portfolio Website",
      description: "Modern React Design",
      imgUrl: projImg3,
      category: "fullstack",
      tech: ["React", "CSS3", "JavaScript"],
      icon: "ðŸŽ¨"
    },
  ];

  const filteredProjects = activeFilter === 'all' ? projects : projects.filter(p => p.category === activeFilter);

  return (
    <section className="project" id="projects">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) =>
              <div className={isVisible ? "animate__animated animate__fadeIn": ""}>
                <h2>Projects</h2>
                <p>30+ professional projects delivered using modern technologies. Full-stack development specializing in web applications, REST APIs, and responsive designs. From enterprise solutions to innovative web platforms.</p>
                <Tab.Container id="projects-tabs" defaultActiveKey="first">
                  <Nav variant="pills" className="nav-pills mb-5 justify-content-center align-items-center" id="pills-tab">
                    <Nav.Item>
                      <Nav.Link eventKey="first">Full Stack</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="second">Laravel</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="third">Java/Spring</Nav.Link>
                    </Nav.Item>
                  </Nav>
                  <Tab.Content id="slideInUp" className={isVisible ? "animate__animated animate__slideInUp" : ""}>
                    <Tab.Pane eventKey="first">
                      <Row>
                        {
                          projects.map((project, index) => {
                            return (
                              <ProjectCard
                                key={index}
                                {...project}
                                />
                            )
                          })
                        }
                      </Row>
                    </Tab.Pane>
                    <Tab.Pane eventKey="second">
                      <p>Laravel projects including STAFFHUB - a comprehensive HR management system with PostgreSQL database, Tailwind CSS styling, and collaborative development using Git and JIRA. Full MVC architecture implementation with advanced database optimization.</p>
                    </Tab.Pane>
                    <Tab.Pane eventKey="third">
                      <p>Java Spring Boot projects including Delivery Optimizer - a sophisticated logistics system featuring REST API architecture, JUnit testing, UML design patterns, and optimization algorithms for efficient route planning and delivery scheduling.</p>
                    </Tab.Pane>
                  </Tab.Content>
                </Tab.Container>
              </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2}></img>
    </section>
  )
}
