import { Col } from "react-bootstrap";

export const ProjectCard = ({ title, description, imgUrl, link, videoLink, tags }) => {
  const isGithubLink = link && link.includes('github.com');
  const buttonText = isGithubLink ? 'View on GitHub' : 'View Project';
  
  return (
    <Col size={12} sm={6} md={4}>
      <div className="proj-imgbx">
        <img src={imgUrl} alt={`${title} project screenshot`} />
        <div className="proj-txtx">
          <h4>{title}</h4>
          {tags && tags.length > 0 && (
            <div className="project-tags">
              {tags.map((tag, index) => (
                <span key={index} className="tag">{tag}</span>
              ))}
            </div>
          )}
          <span>{description}</span>
          <div className="project-links">
            {link && <a href={link} target="_blank" rel="noopener noreferrer" className="project-link">{buttonText}</a>}
            {videoLink && <a href={videoLink} target="_blank" rel="noopener noreferrer" className="project-link project-link-secondary">Watch Demo</a>}
          </div>
        </div>
      </div>
    </Col>
  )
}
